//check if the incoming message is from the Zymatic Recipe Crafter or Beersmith
if(typeof msg.payload.RECIPES.RECIPE[0].ZYMATIC !== 'undefined') {
    //this is a zymatic xml file just move on
    //zymatic = msg.payload.RECIPES.RECIPE[0].ZYMATIC[0];
} else {
    //convert beerXML to zymaticXML

    //get the recipe payload and make it workable
    var js = msg.payload.RECIPES.RECIPE[0];
    JSON.stringify(js);

    //add the empty zymatic array
    js["ZYMATIC"] = [];

    //add the empty step object which is an object of arrays
    js.ZYMATIC[0] = {"STEP":[]};

    //populate the step object with an object containing the step info array
	var ms = js.MASH[0].MASH_STEPS[0].MASH_STEP;
	var firstStep = true;
	var stepTime = 0;
	var lastTemp = 0;

	//BUILD THE MASH SECTION
	//payload.RECIPES.RECIPE[0].MASH[0].MASH_STEPS[0].MASH_STEP[0].NAME
	var stepCount = 0;
	for(let step of Object.keys(ms)) {
	    //var requiresDrain = false;
	    var tempc = Number(ms[step].STEP_TEMP[0]);
        var tempf = (tempc * 9/5) +32;
        tempf = Math.round(tempf *1)/1;
	    stepTime = Number(ms[step].STEP_TIME[0]);
	    stepTime = stepTime.toFixed(0);
	    ms[step].STEP_TIME[0] = stepTime;

		if(firstStep){
			js.ZYMATIC[0].STEP[0] = {"NAME":["Heat to " + ms[step].NAME[0]], "TEMP":[tempf], "TIME":["0"], "LOCATION":["6"], "DRAIN":["0"]};
			stepCount += 1;
			firstStep = false;
			lastTemp = tempf;
		} else if((tempf - lastTemp) > 5) {
		    //last step needs a drain
		    js.ZYMATIC[0].STEP[stepCount-1].DRAIN[0] = "5";

	        //greater than 5 degrees needs a heat step
			js.ZYMATIC[0].STEP.push({"NAME":["Heat to " + ms[step].NAME[0]], "TEMP":[tempf], "TIME":["0"], "LOCATION":["6"], "DRAIN":["0"]});
		    stepCount += 1;
		    lastTemp = tempf;
		}

		js.ZYMATIC[0].STEP.push({"NAME":[ms[step].NAME[0]], "TEMP":[tempf], "TIME":[stepTime], "LOCATION":["1"], "DRAIN":["0"]});
		stepCount += 1;
	}
    //last mash needs a drain step
    js.ZYMATIC[0].STEP[stepCount-1].DRAIN[0] = "8";
    //payload.RECIPES.RECIPE[0].ZYMATIC[0].STEP[3].DRAIN[0]

    //EXTRACT THE BOIL PARAMETERS
    var hs = js.HOPS[0].HOP;
	var numberHops = hs.length;
	var firstHop = true;
	var hopMap = new Map();
	var hc = 1;

	for(let hopTime of Object.keys(hs)) {
	    var ht = Number(hs[hopTime].TIME[0]);
	    hopMap.set('Boil Adjunct ' + hc, ht)
	    hc += 1;
	}

	var one = 0;
	var two = 0;
	var three = 0;

    if (!hopMap.has('Boil Adjunct 4')) {
        hopMap.set('Boil Adjunct 4', '0');
    }
	if (hopMap.has('Boil Adjunct 3') && (Number(hopMap.get('Boil Adjunct 4')) > 0)) {
        three = (Number(hopMap.get('Boil Adjunct 3')) - Number(hopMap.get('Boil Adjunct 4')));
        hopMap.set('Boil Adjunct 3', three);
    } else if(!hopMap.has('Boil Adjunct 3')) {
        //add the Adjunct compartment for use in the calculation
        hopMap.set('Boil Adjunct 3', '0');
    }
    if (hopMap.has('Boil Adjunct 2') && (Number(hopMap.get('Boil Adjunct 3')) > 0)) {
        two = (Number(hopMap.get('Boil Adjunct 2')) - Number(hopMap.get('Boil Adjunct 1')));
        hopMap.set('Boil Adjunct 2', two);
    } else if(!hopMap.has('Boil Adjunct 2')) {
        //add the Adjunct compartment for use in the calculation
        hopMap.set('Boil Adjunct 2', '0');
    }
    if (hopMap.has('Boil Adjunct 1') && (Number(hopMap.get('Boil Adjunct 2')) > 0)) {
        one = (Number(hopMap.get('Boil Adjunct 1')) - Number(hopMap.get('Boil Adjunct 2')));
        hopMap.set('Boil Adjunct 1', one);
    } else if(!hopMap.has('Boil Adjunct 1')) {
        //add the Adjunct compartment for use in the calculation
        hopMap.set('Boil Adjunct 1', '0');
    }

